/*----------------------------------------------------------------------------*/
zrtp_status_t zrtp_sha_test( zrtp_hash_t *self,
							 const uint8_t *test_vector,
							 int vector_length,
							 const uint8_t *test_result,
							 int hash_type)
{
	uint16_t i = 0, result_length = 0;	
	zrtp_status_t res = zrtp_status_fail;
	/*it is enougth for sha1 result (20 bytes) and sha256 result (32 bytes)*/
	zrtp_string32_t hval = ZSTR_INIT_EMPTY(hval);
	zrtp_memset(&hval, 0, sizeof(hval));

	switch(hash_type)
	{
	case ZRTP_HASH_SHA1:
		result_length = SHA1_DIGEST_SIZE;
		break;
	case ZRTP_HASH_SHA256:
		result_length = SHA256_DIGEST_SIZE;
		break;
	default:
		return zrtp_status_bad_param;
	}

	hval.max_length = result_length;

	// TODO: test for non-c version
	res = self->hash_c(self, (const char*)test_vector, vector_length, (zrtp_stringn_t*)&hval);
	if (zrtp_status_ok != res) {
		return res;
	}
	
	for (i = 0; i < result_length; i++) {
		if ((uint8_t)hval.buffer[i] != test_result[i]) {
			return zrtp_status_fail;
		}
	}
	
	return zrtp_status_ok;
}

/*----------------------------------------------------------------------------*/
zrtp_status_t zrtp_hmac_test( zrtp_hash_t *self,
							  const uint8_t *key,
							  uint16_t key_length,
							  const uint8_t *test_vector,
							  uint16_t vector_length,
							  const uint8_t *test_result,
							  int hash_type)
{
	uint16_t i, result_length;
	zrtp_status_t 	res;
	/*it is enougth for sha1 result (20 bytes) and sha256 result (32 bytes)*/
	zrtp_string32_t hval = ZSTR_INIT_EMPTY(hval);
	zrtp_string128_t zrtp_key = ZSTR_INIT_EMPTY(zrtp_key);
	zrtp_string128_t zrtp_test_vector = ZSTR_INIT_EMPTY(zrtp_test_vector);

	zrtp_memset(&hval, 0, sizeof(hval));		

	switch (hash_type) {
		case ZRTP_HASH_SHA1:
			result_length = SHA1_DIGEST_SIZE;
			break;
		case ZRTP_HASH_SHA256:
			result_length = SHA256_DIGEST_SIZE;
			break;
		default:
			return zrtp_status_bad_param;
	}

	hval.max_length = result_length;
	hval.length = 0;
	
	zrtp_memset(&zrtp_key, 0, sizeof(zrtp_key));
	zrtp_memset(&zrtp_test_vector, 0, sizeof(zrtp_test_vector));
	
	zrtp_key.max_length = key_length;
	zrtp_key.length = key_length;
	zrtp_memcpy(zrtp_key.buffer, key, key_length);
	
	zrtp_test_vector.max_length = (uint16_t)vector_length;
	zrtp_test_vector.length = (uint16_t)vector_length;
	zrtp_memcpy(zrtp_test_vector.buffer, test_vector, vector_length);
	
	// TODO: test for C version
	res = self->hmac(self, ZSTR_GV(zrtp_key), ZSTR_GV(zrtp_test_vector), ZSTR_GV(hval));	
	if (zrtp_status_ok != res) {
		return res;
	}

	for (i = 0; i < result_length; i++) {
		if ((uint8_t)hval.buffer[i] != test_result[i]) {
			return zrtp_status_fail;
		}
	}
	
	return zrtp_status_ok;
}

